package day01;

public class Hello {

	public static void main(String[] args) {
		System.out.print("Hello world\n");
		System.out.print("안녕하세요\n");
		System.out.println("1");// 문자열을 사용하려면 ""안에 내용을 적습니다
		System.out.println(1);
		//한줄 주석 - 프로그램 코드를 첨언하는 역할을 합니다.
		/*
		 *  여러줄 주석입니다.
		
		 *  sysout 컨트롤 스페이스하면 빠르게나옴
		 *  단축키 
		 *  코드행이동 = alt + 방향키
		 *  코드행 삭제 컨트롤+d
		 *  되돌리기 컨 +z
		 *  빠른주석 컨+/
		 *  코드 블럭영역 잡기 쉬프트 + 방향키 or end or home
		 *  
		 */
		System.out.println();//자바에서 주로 사용하는 출력문의 형식입니다.
		
		// TODO Auto-generated method stub

	}

}
